var ConvertBase = require('./util/ConvertBase'),
    Render = {}


Render.renderBin = (s, output) => {
    window.localStorage.input = s

    s = s
        .replace(/`/g, '\\`')
        .replace(/\${/g, '\\${')
        .replace(/\\n/g, '\\\\n')
        .replace(/(\r\n|\n|\r)/gm, "");

    let binMode = window.localStorage.getItem('binMode')

    if (binMode === 'binToDec') {
        s = ConvertBase.bin2dec(s)
        output.setValue(s)
    } else if (binMode === 'binToHex') {
        s = ConvertBase.bin2hex(s)
        output.setValue(s)
    }
}

module.exports = Render